import React from 'react';
import { StyleSheet, TouchableOpacity, Text, View, Image } from 'react-native';
import { Ionicons } from '@expo/vector-icons';
import { Component } from './styles';

const CuePoints = ({
  horseIcon,
  horsePoints,
  horseName,
  /* onPress, */
  riderIcon,
  countryFlag,
  riderName,
}: {
  horseIcon: any;
  horsePoints: any;
  horseName: any;
  /* onPress: () => void; */
  riderIcon: any;
  countryFlag: any;
  riderName: any;
}) => {
  return (
    <Component>
      <TouchableOpacity style={styles.horseTab} /* onPress={onPress} */>
        <Image style={styles.horseImage} source={horseIcon} />
        <Text style={styles.points}>{horsePoints}</Text>
        <Text style={styles.mainText}>{horseName}</Text>
        <Ionicons name="ios-arrow-forward" size={24} color="white" style={styles.arrowIcon}/>
      </TouchableOpacity>
      <View style={styles.separator} />
      <TouchableOpacity /* onPress={onPress} */>
        <Image style={styles.horseImage} source={riderIcon} />
        <Text style={styles.flagStyle}>{countryFlag}</Text>
        <Text style={styles.mainText}>{riderName}</Text>
        <Ionicons name="ios-arrow-forward" size={24} color="white" style={styles.arrowIcon}/>
      </TouchableOpacity>
    </Component>
  );
};

const componentWidth = '100%';
const componentHeight = '15%';

const styles = StyleSheet.create({
  component: {
    position: 'relative',
    width: componentWidth,
    height: componentHeight,
    backgroundColor: '#33414b',
    alignItems: 'flex-start',
  },
  horseTab: {
    flex: 1,
    display: 'flex',
  },
  mainText: {
    color: 'white',
    fontWeight: '500',
    marginLeft: 120,
    marginTop: -25,
    fontSize: 18,
  },
  points: {
    color: 'white',
    fontWeight: '500',
    marginLeft: 70,
    marginTop: -20,
    fontSize: 18,
  },
  horseImage: {
    width: 40,
    height: 40,
    marginTop: 10,
    marginLeft: 10,
  },
  separator: {
    marginHorizontal: 20,
    height: 1,
    width: '90%',
    backgroundColor: 'rgba(255, 255, 255, 0.1)',
  },
  arrowIcon: {
    position: 'absolute',
    fontSize: 34,
    marginTop: 10,
    marginLeft: 360
  },
  flagStyle: {
    color: 'white',
    fontWeight: '500',
    marginLeft: 70,
    marginTop: -20,
    fontSize: 18,
  }
});

export default CuePoints;
